/* eslint-disable func-names */ /* eslint-disable no-bitwise */ /* eslint-disable no-plusplus */ import { Image as ImageJS } from &#39;image-js&#39; import { lineURL, pngURL } from &#39;../../components/TokenDeploy/MakeSymbol&#39; const BASE_SIZE = 640 async function makeImage( imgBytes: ArrayBuffer, templateNum: number, borderColor: string ) { // 1. load image from bytes const originalImg = await ImageJS.load(imgBytes) const emptyArr = new Array(640 * 640 * 4).fill(0) const bg = new ImageJS(BASE_SIZE, BASE_SIZE, emptyArr, { alpha: 1 }) console.log( &#39;original originalImg info&#39;, originalImg.width, originalImg.height ) // 2. resize image let xOffset = 0 let yOffset = 0 let resizedImg: ImageJS if (originalImg.width &gt; originalImg.height) { resizedImg = originalImg.resize({ width: BASE_SIZE }) yOffset = (BASE_SIZE - resizedImg.height) / 2 } else { resizedImg = originalImg.resize({ height: BASE_SIZE }) xOffset = (BASE_SIZE - resizedImg.width) / 2 } const squaredImg = await mergeImages(bg, xOffset, yOffset, resizedImg) // 3. make maskImage const maskOriginal = await makeMaskImage(templateNum) const mask: ImageJS = maskOriginal // @ts-ignore .gray() .mask({ threshold: 0.1, useAlpha: true }) // 4. extract by mask // @ts-ignore const maskedImage = await squaredImg.extract(mask, { position: [0, 0] }) // 5. draw border image const borderImg = await makeBorderImage(templateNum, borderColor) const borderAddedImage = await mergeImages(maskedImage, 0, 0, borderImg) // Return as blob type return borderAddedImage.toBlob() } async function makeMaskImage(templateNumber: number): Promise&lt;ImageJS&gt; { const borderMaskFile = pngURL[templateNumber] const maskUrl = `${borderMaskFile}` return new Promise&lt;ImageJS&gt;(function (resolve, reject) { const xhr = new XMLHttpRequest() xhr.open(&#39;get&#39;, maskUrl) xhr.responseType = &#39;arraybuffer&#39; xhr.onload = function () { ImageJS.load(xhr.response).then((mask) =&gt; resolve(mask)) } xhr.onerror = function () { reject(new Error(`failed by ${this.status}`)) } xhr.send() }) } // * Coloring. async function makeBorderImage( templateNumber: number, borderColor: string ): Promise&lt;ImageJS&gt; { const rawBorderMask = await loadBorderImage(templateNumber) // @ts-ignore const mask = rawBorderMask.gray().mask({ threshold: 0.1, useAlpha: true }) const background = new ImageJS(BASE_SIZE, BASE_SIZE, [], { alpha: 1 }) // @ts-ignore const maskedBorder = background.extract(mask, { position: [0, 0] }) const border = maskedBorder.paintMasks(mask, { color: borderColor }) return border } // * Load image. async function loadBorderImage(templateNumber: number): Promise&lt;ImageJS&gt; { const borderMaskFile = lineURL[templateNumber] const borderUrl = `${borderMaskFile}` return new Promise&lt;ImageJS&gt;(function (resolve, reject) { const xhr = new XMLHttpRequest() xhr.open(&#39;get&#39;, borderUrl) xhr.responseType = &#39;arraybuffer&#39; xhr.onload = function () { ImageJS.load(xhr.response).then((mask) =&gt; resolve(mask)) } xhr.onerror = function () { reject(new Error(`failed by ${this.status}`)) } xhr.send() }) } async function mergeImages( out: ImageJS, x: number, y: number, toInsert: ImageJS ) { const maxY = Math.min(out.height, y + toInsert.height) const maxX = Math.min(out.width, x + toInsert.width) if (out.bitDepth === 1) { for (let j = y; j &lt; maxY; j++) { for (let i = x; i &lt; maxX; i++) { const val = toInsert.getBitXY(i - x, j - y) if (val) out.setBitXY(i, j) else out.clearBitXY(i, j) } } } else { for (let j = y; j &lt; maxY; j++) { for (let i = x; i &lt; maxX; i++) { if (toInsert.getPixelXY(i - x, j - y)[3] &gt; 127) { out.setPixelXY(i, j, toInsert.getPixelXY(i - x, j - y)) } } } } return out } export default makeImage const dataURLtoFile = (base64: string, fileName: string) =&gt; { const arr = base64.split(&#39;,&#39;) const mime = arr[0].match(/:(.*?);/)[1] const bstr = window.atob(arr[1]) let n = bstr.length const u8arr = new Uint8Array(n) while (n--) { u8arr[n] = bstr.charCodeAt(n) } const file = new File([u8arr], `${fileName}.png`, { type: mime }) return file }

